let idleTime=0;function timerIncrement(){idleTime++;let e=getRootFolder();1===idleTime&&(window.location.href=e+"logout.php?inactivity")}function getRootFolder(){let e=window.location.href,t=new URL(e),n=t.origin,i=t.pathname.split("/");if(i.length<=1)return n;let o=`${n}/${i[1]}/`;return o}document.addEventListener("DOMContentLoaded",function(){setInterval(timerIncrement,6e4),document.addEventListener("mousemove",function(){idleTime=0}),document.addEventListener("keypress",function(){idleTime=0})});